// import axios from 'axios'
import axios from 'axios'
import { createStore } from 'vuex'

export const store = createStore({
  state () {
    return {
     api:'http://192.168.3.68:2525',
     i:1,
     DirPath:'',
     dialogVisible:false,
     FilesTyoes:'',//获取文件类型，
     DirsFileList:[],
     uploadPercentage:0,
     files:[],
     downZhi:'',
     OfficEword:'',
     otherDirsType:[],
     VideoPlayercontrols:true
    }
  },
  actions:{
    DirsFileList(){
      axios({
        url: this.$store.state.api + '/' + 'DirsFileList',
        method: "post",
        params: {
            'DirsFileList': 'no'
        }
      }).then(res => {
        this.$store.state.DirsFileList = res.data.data
        for (let i = 0; i < this.$store.state.DirsFileList.length; i++) {
            this.$store.state.DirsFileList[i] = { 'index': i, 'data': this.$store.state.DirsFileList[i], "FileType": false }
        }
      })


    }
  }

})
